	uint8 region_group = wlc_phy_get_locale(pi->rxgcrsi);

	is_phybw40 = CHSPEC_IS40(pi->radio_chanspec);

	pi_nphy = pi->u.pi_nphy;

	PHY_TRACE(("wl%d: %s\n", pi->sh->unit, __FUNCTION__));

