			uint16 clipmdB2 = 0x0104;
			/* LO Gain	*/
			uint16 cliploA1 = 0x42;
			uint16 cliploA2 = 0x42;
			uint16 cliploB1 = 0x0108;
			uint16 cliploB2 = 0x0108;
			/* InitGCode */
			uint16 initGCA1 = 0x8E;
			uint16 initGCA2 = 0x8E;
			/* InitGain */
			uint16 initgain[] = {0x6247, 0x6247};

